import React from 'react';
import axios from 'axios';
import { useEffect } from 'react';
import { useState } from 'react';

const LiveNews = () => {
  // let[meals, setAllData] =useState([]);
  // let [Image, setImage] = useState();
  // let [Title, setTitle] = useState();
  // let [Des, setDes] = useState();
  const [data, setData] = useState([]);
  const getData = async () => {
    const { data } = await axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?f=a`);
    setData(data);
  };
  useEffect(() => {
    getData();
  }, []);
 console.log(data)
//   useEffect(() => {
//     axios
//       .get('https://www.themealdb.com/api/json/v1/1/search.php?f=a')
//       .then((response) => setAllData(response.data))
//   }, []);

// console.log(meals)

  
  return (
    <div className="container-fluid text-center bg-grey">
      <h2>Portfolio</h2>
      <h4>What we have created</h4>
      <div className="row text-center">
        {data.meals.map((element) => (
          <div className="col-sm-4 p-2">
          <div className="card w-75">
            <img src={element.strMealThumb} className="card-img-top" alt="..." />
            <div className="card-body">
              <h5 className="card-title">{element.strMeal}</h5>
              <p className="card-text">
              {element.strInstructions}
              </p>
            </div>
          </div>
        </div>
))}
      </div>
      
    </div>
  );
};

export default LiveNews;
