import {
 createStore,
  applyMiddleware,
  combineReducers,
} from 'redux';
import thunk from 'redux-thunk';
import Rockets from '../components/Rockets/Rockets';

const reducer = combineReducers({ Rockets });

const store = createStore(reducer, {}, applyMiddleware(thunk));

export default store;
