const BASE_URL = 'https://community-hacker-news-v1.p.rapidapi.com/topstories.json?print=pretty';

const bookReducer = (state = [], action) => {
  switch (action.type) {
    case ADD_BOOKS:
      return [
        ...state,
        { data: action.data
          // id: action.id,
          // img: action.img,
