/* eslint-disable react-hooks/exhaustive-deps */
import React from 'react';
import axios from 'axios';
import { useEffect } from 'react';
import { useState } from 'react';
import RotateRightIcon from '@mui/icons-material/RotateRight';

const LiveNews = () => {
  const [data, setData] = useState([]);
  const getData = async () => {
    const { data } = await axios.get(`https://www.themealdb.com/api/json/v1/1/search.php?f=a`);
    setData(data);
  };
  useEffect(() => {
    setTimeout(() => {
      getData();
      console.log('This will run after 1 second!')
    }, 2000);
  }, []);
 console.log(data)
  // useEffect(() => {
  //   axios
  //     .get('https://www.themealdb.com/api/json/v1/1/search.php?f=a')
  //     .then((response) => setData(response.data))
  // }, 1000);

// console.log(meals)

  
  return (
    <div className="container-fluid text-center bg-grey">
      <h2>Big Chef</h2>
      <h4>Best Quality products
From around the world</h4>
      <div className="row text-center">
        {data ==="undefined" ?  data.meals.for((element) => (
          <div className="col-sm-4 p-2" key={element.idMeal}>
          <div className="card w-75">
            <img src={element.strMealThumb} className="card-img-top" alt="..." />
            <div className="card-body">
              <h5 className="card-title">{element.strMeal}</h5>
              <p className="card-text">
              {element.strInstructions}
              </p>
            </div>
          </div>
        </div>
)) :  <RotateRightIcon className="w-50"/> }
      </div>
     
    </div>
  );
};

export default LiveNews;
