const AddNumbers = () => {
  const [Result, setResult] = useState(0);

  const handleForm = (e) => {
    e.preventDefault();
    const { firstnum, secondnum } = e.target.elements;
    const first = parseInt(firstnum.value);
    const second = parseInt(secondnum.value);
    const res = first + second;
    setResult(res);
    firstnum.value = '';
    secondnum.value = '';
  };
