/* eslint-disable no-console */


const ADD_BOOKS = 'ADD_BOOKS';
const BASE_URL = 'https://www.themealdb.com/api/json/v1/1/search.php?f=a';

const dataReducer = (state = [], action) => {
  switch (action.type) {
    case ADD_BOOKS:
      return [
        ...state,
        {
          id: action.id,
          img: action.img,
          title: action.title,
          des: action.des,
        },
      ];

    default:
      return state;
  }
};

export const additem = (id, img, title, des) => ({
  type: ADD_BOOKS,
  id,
  img,
  title,
  des,
});



export async function fetchBooks(dispatch) {
  const response = await fetch(BASE_URL);
  const data = await response.json();
  data.meals.forEach((element) => dispatch(additem( 
 element.idMeal,
element.strMealThumb,
element.strMeal,
element.strInstructions,
  

  )));
}


export default dataReducer;
